import React, { useState, useEffect } from 'react';
import Pear from '../images/Pear.png';
import Renn from '../images/Renn.png';
import Softbank from '../images/Softbank.png';
import Think from '../images/Think.png';
import WIL from '../images/WIL.png';
import ZhenFund from '../images/ZhenFund.png';
import './SponsorsBanner.css'; 

const SponsorsBanner = () => {
  const images = [
    Pear, Renn, Softbank, Think, WIL, ZhenFund
  ];

  // State to keep track of the currently displayed image index
  const [currentIndex, setCurrentIndex] = useState(0);

  // Use the useEffect hook to handle the carousel movement
  useEffect(() => {
    // Function to update the currentIndex and create a continuous loop
    const moveCarousel = () => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    };

    // Set an interval to call the moveCarousel function every 10 seconds
    const intervalId = setInterval(moveCarousel, 7000);

    // Clear the interval on component unmount to avoid memory leaks
    return () => clearInterval(intervalId);
  }, [images.length]);

  return (
    <div className="horizontal-carousel">
      {images.map((image, index) => (
        <img
          key={index}
          src={image}
          alt={`Sponsor Logos`}
          style={{padding: 30, width: "100%"}}
        />
      ))}
    </div>
  );
};

export default SponsorsBanner;
