import React, { useState, useEffect, useRef } from 'react';
import Pear from '../images/Pear.png';
import Renn from '../images/Renn.png';
import Softbank from '../images/Softbank.png';
import Think from '../images/Think.png';
import WIL from '../images/WIL.png';
import ZhenFund from '../images/ZhenFund.png';
import './SponsorsBanner.css'; 

const SponsorsBanner = () => {
  const images = [
    Pear, Renn, Softbank, Think, WIL, ZhenFund
  ];

  const carouselRef = useRef(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const carouselContainer = carouselRef.current;
    const imageWidth = carouselContainer.offsetWidth;

    const moveCarousel = () => {
      setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
    };

    const intervalId = setInterval(moveCarousel, 10000);

    return () => clearInterval(intervalId);
  }, [images.length]);

  return (
    <div className="carousel-container">
      <div
        className="horizontal-carousel"
        ref={carouselRef}
        style={{
          transform: `translateX(-${currentIndex * 100}%)`,
          transition: 'transform 1s ease',
        }}
      >
        {images.map((image, index) => (
          <img key={index} src={image} alt={`Image ${index + 1}`} />
        ))}
      </div>
    </div>
  );
};

export default SponsorsBanner;
