import React, { useState, useEffect } from "react";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";

const TypingComponent = ({ text, noDelete }) => {
  const [displayText, setDisplayText] = useState("");
  const [typingIndex, setTypingIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);

  useEffect(() => {
    const typingSpeed = 100; // Adjust typing speed in milliseconds
    const deleteSpeed = 100; // Adjust deleting speed in milliseconds
    const delay = isDeleting ? deleteSpeed : typingSpeed;

    const timeoutId = setTimeout(() => {
      if (isDeleting && !noDelete) {
        setDisplayText((prevText) => prevText.slice(0, -1));
        if (displayText === "") {
          setIsDeleting(false);
          setTypingIndex((prevIndex) => (prevIndex + 1) % text.length);
        }
      } else {
        setDisplayText((prevText) => text.slice(0, prevText.length + 1));
        if (displayText === text) {
          setIsDeleting(true);
        }
      }
    }, delay);

    return () => clearTimeout(timeoutId);
  }, [displayText, isDeleting, text]);

  return (
    <Box textAlign="center">
      <Typography sx={{}}>
        {displayText}
      </Typography>
    </Box>
  );
};

export default TypingComponent;
